    private void btn_entrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_entrarActionPerformed
        boolean loginStatus=false;
        UsuarioDAO dao = new UsuarioDAO();
        for (Usuario u : dao.listAll()) {
            if (u.getUsername().equals(tf_usuario.getText()) && u.getPassword().equals(pf_senha.getText())) {
                JOptionPane.showMessageDialog(null, "Bem vindo " + tf_usuario.getText());
                setVisible(false);
                TelaAdmin tela1 = new TelaAdmin();
                tela1.setVisible(true);
                loginStatus=true;
            }
        } if (loginStatus==false){
                    JOptionPane.showMessageDialog(null, "Usuario ou senha errados!", "Alerta", JOptionPane.ERROR_MESSAGE);
               }
        tf_usuario.setText("");
        pf_senha.setText("");
    }//GEN-LAST:event_btn_entrarActionPerformed

