    private void pf_senhaKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_pf_senhaKeyPressed
        boolean loginStatus=false;
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            UsuarioDAO dao = new UsuarioDAO();
            for (Usuario u : dao.listAll()) {
                if (u.getUsername().equals(tf_usuario.getText()) && u.getPassword().equals(pf_senha.getText())) {
                    JOptionPane.showMessageDialog(null, "Bem vindo " + tf_usuario.getText());
                    setVisible(false);
                    TelaAdmin tela1 = new TelaAdmin();
                    tela1.setVisible(true);
                    loginStatus=true;
                }
            }if (loginStatus==false) {
                   JOptionPane.showMessageDialog(null, "Usuario ou senha errados!", "Alerta", JOptionPane.WARNING_MESSAGE);
                }
            tf_usuario.setText("");
            pf_senha.setText("");
        }
    }//GEN-LAST:event_pf_senhaKeyPressed


